/* * @(#)StateEvent.java * Copyright Â© 2020 Werner Randelshofer, Switzerland. MIT License. */package ch.randelshofer.util;import java.util.EventObject;/** * Event for state changes. * * @author Werner Randelshofer, Staldenmattweg 2, CH-6405 Immensee, Switzerland * @version 1.0  1999-10-19 */public class StateEvent        extends EventObject {    private final static long serialVersionUID = 1L;    /**     * State.     */    private int state;    public StateEvent(Object source, int state) {        super(source);        this.state = state;    }    public int getNewState() {        return state;    }}